INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10543, 8679, 'Neomycin (topical)', 'Tympanic Membrane Perforation', 'Like other aminoglycosides, neomycin is potentially ototoxic, both to vestibular and to auditory functions.  Therapy with neomycin otic preparations should be administered cautiously in patients with chronic otitis media or a perforated tympanic membrane.  The risk of ototoxicity may be increased if medication enters the middle ear.', '3', '', 'DDInter', 0);
