INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10559, 8679, 'Neomycin', 'Gastroenteritis', 'The use of neomycin oral solution is contraindicated in patients with inflammatory or ulcerative gastrointestinal diseases because of the potential for enhanced absorption of neomycin.  Like other aminoglycosides, neomycin is potentially oto- and nephrotoxic, and systemic exposure should be avoided if possible.', '3', '', 'DDInter', 0);
