INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10588, 27924, 'Nilotinib', 'Long QT Syndrome', 'Nilotinib should not be used in patients with hypokalemia, hypomagnesemia or long QT syndrome, as it has shown to prolong QT interval in a concentration-dependent manner.  This can cause a ventricular tachycardia called Torsade de Pointes which can result in syncope, seizures, and/or death.  Before initiating nilotinib, electrolytes should be tested.  Hypokalemia or hypomagnesemia should be corrected before starting treatment and monitored periodically.  Patients at risk should be monitored with an EKG before and during treatment as clinically indicated and dose adjustments might be needed.', '3', '', 'DDInter', 0);
