INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10590, 27924, 'Nilotinib', 'Cardiovascular Diseases', 'Cardiovascular events, including arterial vascular occlusive events, peripheral arterial occlusive events and ischemic cerebrovascular events have been reported in patients receiving tyrosine kinase inhibitors.  If acute signs or symptoms of cardiovascular events occur, patients should seek immediate medical attention.  The cardiovascular status and risk factors of patients should be evaluated prior to therapy and cardiovascular monitoring and management should take place during treatment.', '2', '', 'DDInter', 0);
