INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10592, 27924, 'Nilotinib', 'Water-Electrolyte Imbalance', 'Fluid retention occurs with BCR-ABL tyrosine kinase inhibitors therapy and may manifest as pericardial effusion, pleural effusion, pulmonary edema, and/or peripheral edema.  Caution should be taken when using these drugs in patients with preexisting fluid retention or congestive heart failure.  Monitor and manage patients using standards of care.  Interrupt, reduce dose or discontinue therapy as necessary.', '2', '', 'DDInter', 0);
