INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10594, 27924, 'Nilotinib', 'Bone Marrow Failure Disorders', 'Thrombocytopenia, aplastic anemia, agranulocytosis and neutropenia occur with BCR-ABL tyrosine kinase inhibitors.  Therapy with these drugs should be administered cautiously in patients with preexisting bone marrow suppression.  A complete blood count should be performed every 1-2 weeks for the first month of therapy and then monthly thereafter, or as clinically indicated.  To manage myelosuppression, withhold, reduce dose, or discontinue therapy as necessary.', '2', '', 'DDInter', 0);
