INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10601, 23974, 'Nintedanib', 'Lung Diseases', 'The use of certain multikinase inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.  Immediately withhold treatment in patients diagnosed with ILD/pneumonitis and permanently discontinue therapy if no other potential causes of ILD/pneumonitis have been identified.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10602, 23973, 'Nintedanib', 'Thromboembolism', 'Arterial thromboembolic events, including myocardial infarction have been reported in patients taking nintedanib.  It is recommended to exercise caution when treating patients at higher cardiovascular risk, including known coronary artery disease and to consider treatment interruption in patients who develop signs or symptoms of acute myocardial ischemia.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10603, 23974, 'Nintedanib', 'Thromboembolism', 'Arterial thromboembolic events, including myocardial infarction have been reported in patients taking nintedanib.  It is recommended to exercise caution when treating patients at higher cardiovascular risk, including known coronary artery disease and to consider treatment interruption in patients who develop signs or symptoms of acute myocardial ischemia.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10604, 23973, 'Nintedanib', 'Hemorrhage', 'Nintedanib may increase the risk of bleeding.  Nintedanib should only be used in patients with known risk of bleeding if the anticipated benefit outweighs the potential risk.  Close monitoring is recommended.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10605, 23974, 'Nintedanib', 'Hemorrhage', 'Nintedanib may increase the risk of bleeding.  Nintedanib should only be used in patients with known risk of bleeding if the anticipated benefit outweighs the potential risk.  Close monitoring is recommended.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10606, 23973, 'Nintedanib', 'Peptic Ulcer Perforation', 'Nintedanib may increase the risk of gastrointestinal perforation.  Use caution when treating patients who have had recent abdominal surgery.  It is recommended to discontinue therapy with nintedanib in patients who develop gastrointestinal perforation.  Nintedanib should only be used in patients with known risk of gastrointestinal perforation if the anticipated benefit outweighs the potential risk.  Care and close monitoring is recommended.', '2', '', 'DDInter', 0);
