INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10608, 23973, 'Nintedanib', 'Hepatic Insufficiency', 'The use of nintedanib is associated with elevations of liver enzymes.  Treatment with nintedanib is not recommended in patients with moderate or severe hepatic impairment.  Patients with mild hepatic impairment can be treated with a reduced dose of nintedanib.  It is recommended to conduct liver function tests prior to treatment with nintedanib and periodically thereafter as clinically indicated.  Dosage modifications or interruption may be necessary for liver enzyme elevations as clinically indicated and according to medical practices.', '2', '', 'DDInter', 0);
