INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10610, 23973, 'Nintedanib', 'Kidney Diseases', 'Based on a population pharmacokinetics analysis of nintedanib, exposure to nintedanib is not influenced by mild or moderate renal impairment.  No dose adjustment is required in patients with mild to moderate renal impairment.  Care and close monitoring is recommended when using this agent in patients with severe renal impairment and end-stage renal disease as the safety, efficacy, and, pharmacokinetics of nintedanib have not been studied these patients.', '2', '', 'DDInter', 0);
