INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10864, 1064, 'Atenolol', 'Pheochromocytoma', 'Administration of beta-blockers alone in the setting of pheochromocytoma has been associated with a paradoxical increase in blood pressure due to the attenuation of beta-mediated vasodilatation in skeletal muscle.  In patients with pheochromocytoma, an alpha-blocking agent should be initiated prior to the use of any beta-blocking agent.  Caution should be taken in the administration of these agents to patients suspected of having pheochromocytoma.', '2', '', 'DDInter', 0);
