INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10942, 8827, 'Nicotine', 'Pheochromocytoma', 'Nicotine causes the release of catecholamines from the adrenal medulla.  Enhanced sympathetic activity can provoke hypertensive crises in patients with pheochromocytoma or other tumors of the adrenal medulla, such as some neuroblastomas.  Therapy with nicotine should be administered cautiously in patients with these tumors.', '3', '', 'DDInter', 0);
