INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10990, 7653, 'Nitrofurantoin', 'Kidney Diseases', 'The use of nitrofurantoin is contraindicated in patients with anuria, oliguria, or significant renal impairment (CrCl < 60 mL/min).  Urinary concentration of the drug is likely to be inadequate in these patients, increasing the risk of therapeutic failure.  Since nitrofurantoin is eliminated by the kidney, risk of toxicity may also be increased.', '3', '', 'DDInter', 0);
