INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10998, 7653, 'Nitrofurantoin', 'Peripheral Nervous System Diseases', 'The use of nitrofurantoin has occasionally been associated with the development of peripheral neuropathy, which may be severe and irreversible.  Patients at risk include the elderly and those with renal impairment, anemia, diabetes mellitus, electrolyte imbalance, vitamin B12 deficiency, diabetes, and/or debilitating diseases.  Therapy with nitrofurantoin should be administered cautiously in patients with one or more risk factors and/or preexisting peripheral neuropathy.  Periodic monitoring of renal function is recommended during prolonged therapy.', '2', '', 'DDInter', 0);
