INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11218, 4278, 'Nifedipine', 'Shock, Cardiogenic', 'In general, calcium channel blockers (CCBs) should not be used in patients with hypotension (systolic pressure < 90 mm Hg) or cardiogenic shock.  Due to potential negative inotropic and peripheral vasodilating effects, the use of CCBs may further depress cardiac output and blood pressure, which can be detrimental in these patients.  The use of verapamil and diltiazem is specifically contraindicated under these circumstances.', '3', '', 'DDInter', 0);
