INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11238, 4278, 'Nifedipine', 'Myocardial Infarction', 'Clinical trials studying the use of immediate-release nifedipine in patients who had just sustained myocardial infarctions have not demonstrated any benefit.  In fact, in some trials, patients who received immediate-release nifedipine had significantly worse outcomes than patients who received placebo.  The manufacturers state that immediate-release formulations of nifedipine should not be administered for 1 week after myocardial infarction.  They should also be avoided in the setting of acute coronary syndrome, when infarction may be imminent.', '3', '', 'DDInter', 0);
