INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11248, 4278, 'Nifedipine', 'Kidney Diseases', 'Although the clearance of nifedipine is not dependent on renal function, use of the drug in patients with uremia has been associated with enhanced pharmacologic effects, possibly due to increased sensitivity to the drug or reduced protein binding.  Rarely, reversible elevations in BUN and serum creatinine have been reported in patients with preexisting chronic renal insufficiency given nifedipine, although a causal relationship has not been established.  Nephritis and renal failure have also been observed.  Therapy with nifedipine should be administered cautiously in patients with significantly impaired renal function.', '2', '', 'DDInter', 0);
