INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11267, 23613, 'Nitroprusside', 'Optic Atrophy, Hereditary, Leber', 'The use of nitroprusside is contraindicated in patients with defective or absent rhodanase activity, such as patients with congenital (Leber''s) optic atrophy or with tobacco amblyopia.', '3', '', 'DDInter', 0);
