INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11271, 23613, 'Nitroprusside', 'Anemia', 'When sodium nitroprusside is used to control hypotension during anesthesia, the patient''s capacity to compensate for anemia and hypovolemia may be diminished.  If possible, preexisting anemia and hypovolemia should be corrected prior to administration of sodium nitroprusside.', '2', '', 'DDInter', 0);
