INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11301, 24133, 'Nivolumab', 'Tumor Lysis Syndrome', 'Tumor lysis syndrome (TLS) has occurred in patients receiving certain monoclonal antibodies.  Patients with high tumor burden and those with high circulating lymphocyte counts of greater than 25 X 10^9/L have a higher risk of developing TLS.  Consider tumor lysis prophylaxis prior to the infusion with anti-hyperuricemics and hydration beginning 12 to 24 hours prior to infusion.  It is recommended to correct electrolytes abnormalities, and monitor renal function in patients who develop TLS.  Monitor for signs and symptoms of TLS and temporary interruption or discontinuation of therapy might be required.', '3', '', 'DDInter', 0);
