INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11361, 4285, 'Norfloxacin', 'Crystalluria', 'Crystalluria has been reported rarely during quinolone therapy.  Although it is not expected to occur under normal circumstances with usual recommended dosages, patients who are dehydrated (e.g., due to severe diarrhea or vomiting) may be at increased risk and should be encouraged to consume additional amounts of liquid or given intravenous fluid to ensure an adequate urinary output.  Alkalinity of the urine should be avoided, since it may also increase the risk of crystalluria.  Renal function tests should be performed periodically during prolonged therapy (> 2 weeks).', '2', '', 'DDInter', 0);
