INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11480, 358, 'Obeticholic acid', 'Liver Failure', 'In postmarketing reports, hepatic decompensation and failure, in some cases fatal, have been reported in patients with primary biliary cholangitis (PBC) with decompensated cirrhosis or Child-Pugh Class B or C hepatic impairment when obeticholic acid was dosed more frequently than recommended.  The recommended starting dosage is 5 mg once weekly for patients with Child-Pugh Class B or C hepatic impairment or a prior hepatic decompensation event.  Patients in treatment should be closely monitored for progression of PBC disease, including liver- related complications, with laboratory and clinical assessments.  Dosage adjustment, interruption or discontinuation may be required.', '3', '', 'DDInter', 0);
