INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11484, 358, 'Obeticholic acid', 'Hyperlipidemias', 'Patients with primary biliary cholangitis generally exhibit hyperlipidemia characterized by a significant elevation in total cholesterol primarily due to increased levels of high-density lipoprotein-cholesterol (HDL-C).  Exercise care when using obeticholic acid in patients with lipids disorder.  It is recommended to monitor patients for changes in serum lipid levels during treatment with obeticholic acid.  For patients who do not respond to treatment after 1 year at the highest recommended dosage that can be tolerated (maximum of 10 mg once daily), and who experience a reduction in HDL-C, weigh the potential risks against the benefits of continuing treatment.', '2', '', 'DDInter', 0);
