INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11787, 10188, 'Octreotide', 'Gallbladder Diseases', 'Octreotide inhibits gallbladder contractility and may alter bile composition.  An average of 29% of patients develop new gallstones during octreotide therapy, and up to 20% of patients develop biliary sludge.  Associated pancreatitis has been reported in rare cases.  Gallbladder hypercontractility, sometimes resulting in biliary colic, has been reported upon withdrawal of octreotide.', '2', '', 'DDInter', 0);
