INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11801, 26499, 'Octreotide', 'Heart Diseases', 'Bradycardia, arrhythmias and conduction abnormalities, including QT prolongation have been reported with the use of octreotide.  Patients taking other drugs that have bradycardic effects may need dose adjustments.  Caution is recommended in patients with cardiac function abnormalities.', '2', '', 'DDInter', 0);
