INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11807, 10188, 'Octreotide', 'Hypothyroidism', 'Rarely, hypothyroidism requiring thyroid hormone replacement may occur during chronic therapy.  More commonly, a statistically significant, but usually clinically insignificant, increase in TSH may be observed, possibly caused by slight inhibition of peripheral deiodination of thyroxin.  Therapy with octreotide should be administered cautiously to patients with uncontrolled thyroid disorders.  Clinical monitoring of thyroid function is recommended.', '2', '', 'DDInter', 0);
