INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (12956, 52, 'Olanzapine', 'Hypotension', 'The use of atypical antipsychotic agents has been associated with orthostatic hypotension and syncope.  Therapy with atypical antipsychotics should be administered cautiously in patients with hypotension or conditions that could be exacerbated by hypotension, such as a history of myocardial infarction, angina, or ischemic stroke.  Patients with dehydration (e.g., due to severe diarrhea or vomiting) may be predisposed to hypotension and should also be managed carefully during therapy with atypical antipsychotics.  Blood pressure should be monitored at regular intervals, particularly during dosage escalation or whenever dosage has been altered, and patients should be advised not to rise abruptly from a sitting or recumbent position.', '2', '', 'DDInter', 0);
