INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13301, 30190, 'Olanzapine', 'Phenylketonurias', 'Zyprexa Zydis (brand of olanzapine orally distintegrating tablets) contains 0.34 mg and 0.45 mg of phenylalanine per each 5 mg and 10 mg tablet, respectively.  The phenylalanine content should be considered when this and similar products are used in patients who must restrict their intake of phenylalanine (i.e. phenylketonurics).', '2', '', 'DDInter', 0);
