INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13363, 1166, 'Ondansetron', 'Liver Diseases', 'Ondansetron is extensively metabolized by the liver.  The plasma clearance of ondansetron may be substantially decreased and the half-life prolonged in patients with impaired hepatic function.  During clinical trials in patients receiving concomitant chemotherapy, transient elevations of serum transaminases and isolated cases of jaundice have been reported.  Therapy with ondansetron should be administered cautiously at reduced dosages in patients with liver disease.  The manufacturer recommends a maximum daily dosage of 8 mg in severe hepatic impairment.', '2', '', 'DDInter', 0);
