INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13475, 1116, 'Opium', 'Intracranial Hypertension', 'The hypoventilation associated with administration of opiate agonists, particularly by the intravenous route, can induce cerebral hypoxia and vasodilatation with resultant increase in intracranial pressure.  Opiate agonists should not be used in patients with suspected or known head injury or increased intracranial pressure.  Also, clinicians treating such patients should be aware that opiate agonists may interfere with the evaluation of CNS function, especially with respect to consciousness levels, respiratory status, and pupillary changes.', '3', '', 'DDInter', 0);
