INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13491, 1116, 'Opium', 'Adrenal Insufficiency', 'Patients with Addison''s disease may have increased risk of respiratory depression and prolonged CNS depression associated with the use of narcotic (opioid) analgesic agents.  Conversely, these agents may cause or potentiate adrenal insufficiency.  Therapy with opioids should be administered cautiously and initiated at reduced dosages in patients with adrenocortical insufficiency.  Subsequent doses should be titrated based on individual response rather than a fixed dosing schedule.', '2', '', 'DDInter', 0);
