INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13523, 1116, 'Opium', 'Epilepsy', 'Narcotic (opioid) analgesic agents may exacerbate seizures in patients with seizure disorders and, at higher dosages, have been reported to induce seizures in patients without previous history of seizures.  The proconvulsant activity may be the greatest with meperidine, the active metabolite of which is thought to be responsible.  Therapy with opioids should be administered cautiously in patients with or predisposed to seizures.', '2', '', 'DDInter', 0);
