INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13621, 8977, 'Orphenadrine', 'Anticholinergic Syndrome', 'Orphenadrine has mild anticholinergic activity, to which elderly patients are particularly sensitive.  The manufacturer considers the use of orphenadrine to be contraindicated in patients with glaucoma, pyloric or duodenal obstruction, achalasia, prostatic hypertrophy, bladder neck obstruction, or myasthenia gravis.  Orphenadrine should be used cautiously in other conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention and gastrointestinal obstructive disorders.', '3', '', 'DDInter', 0);
