INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13677, 3958, 'Orlistat', 'Fat Soluble Vitamin Deficiency', 'At the recommended therapeutic dosage of 120 mg three times a day, orlistat inhibits dietary fat absorption by approximately 30% and has been shown to reduce the absorption of some fat-soluble vitamins and beta-carotene.  Therapy with orlistat should be administered cautiously in patients with preexisting deficiency of one or more fat-soluble vitamins (A, D, E and K).  Ideally, the deficiency should be corrected prior to initiation of therapy.  A multivitamin supplement containing fat-soluble vitamins is recommended during therapy, best taken once a day at least 2 hours before or after the administration of orlistat.', '2', '', 'DDInter', 0);
