INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13801, 13991, 'Oxacillin', 'Kidney Diseases', 'Oxacillin is partially converted by the liver to active and inactive metabolites, and both parent drug and metabolites are eliminated by the kidney.  The serum concentrations of oxacillin and its metabolites may be increased and the half-lives prolonged in patients with significantly impaired renal function.  In general, dosage adjustments are not necessary in either renal or hepatic impairment, but the lower range of the usual recommended dosage may be appropriate in patients with severe renal impairment (CrCl < 10 mL/min).  Renal and liver function tests should be performed periodically during prolonged therapy.', '2', '', 'DDInter', 0);
