INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13879, 24385, 'Oxaprozin', 'Heart Failure', 'Fluid retention and edema have been observed in patients treated with nonsteroidal anti-inflammatory drugs (NSAIDs), including some topical formulations.  These drugs should be avoided in patients with severe heart failure unless the benefits are expected to outweigh the risk of worsening heart failure.  If an NSAID is used in patients with severe heart failure, they should be monitored for signs of worsening heart failure.', '2', '', 'DDInter', 0);
