INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14055, 5888, 'Oxaliplatin', 'Kidney Diseases', 'The major route of platinum elimination is renal excretion.  The exposure (AUC) of unbound platinum in plasma ultrafiltrate tends to increase in renally impaired patients.  The starting dose of oxaliplatin should be reduced in patients with severe renal impairment (creatinine clearance < 30 mL/min).  Caution and close monitoring should be exercised when oxaliplatin is administered to patients with renal impairment.', '2', '', 'DDInter', 0);
