INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14094, 9483, 'Oxazepam', 'Alcoholic Intoxication', 'The use of benzodiazepines with alcohol is not recommended.  Patients with acute alcohol intoxication exhibit depressed vital signs.  The central nervous system depressant effects of benzodiazepines may be additive with those of alcohol, and severe respiratory depression and death may occur.  Therapy with benzodiazepines should be administered cautiously in patients who might be prone to acute alcohol intake.', '3', '', 'DDInter', 0);
