INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14101, 24395, 'Oxazepam', 'Glaucoma, Angle-Closure', 'The manufacturers consider the use of benzodiazepines to be contraindicated in patients with acute angle-closure glaucoma or untreated open-angle glaucoma.  These agents do not possess anticholinergic activity but have very rarely been associated with increased intraocular pressure.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14102, 9483, 'Oxazepam', 'Substance-Related Disorders', 'Benzodiazepines have the potential to cause dependence and abuse.  Tolerance as well as physical and psychological dependence can develop, particularly after prolonged use and/or excessive dosages.  However, abrupt cessation following continual use of as few as 6 weeks at therapeutic levels has occasionally precipitated withdrawal symptoms.  Addiction- prone individuals, such as those with a history of alcohol or substance abuse, should be under careful surveillance when treated with benzodiazepines.  It may be prudent to refrain from dispensing large quantities of medication to these patients.  After prolonged use or if dependency is suspected, withdrawal of benzodiazepine therapy should be undertaken gradually using a dosage- tapering schedule.  If withdrawal symptoms occur, temporary reinstitution of benzodiazepines may be necessary.', '3', '', 'DDInter', 0);
