INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14122, 9483, 'Oxazepam', 'Depressive Disorder', 'Benzodiazepines depress the central nervous system and may cause or exacerbate mental depression and cause suicidal behavior and ideation.  Episodes of mania and hypomania have also been reported in depressed patients treated with some of these agents.  Therapy with benzodiazepines should be administered cautiously in patients with a history of depression or other psychiatric disorders.  Patients should be monitored for any changes in mood or behavior.  It may be prudent to refrain from dispensing large quantities of medication to these patients.', '2', '', 'DDInter', 0);
