INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14179, 9475, 'Oxcarbazepine', 'Hyponatremia', 'Some anticonvulsants can cause clinically significant hyponatremia (Na < 125 mmol/L).  Therapy with these drugs should be administered cautiously in patients with conditions predisposing to hyponatremia, such as SIADH, use of diuretics or drugs associated with inappropriate antidiuretic hormone secretion, adrenal insufficiency, hypothyroidism, primary polydipsia, and edema (e.g., due to liver cirrhosis, congestive heart failure, or nephrotic syndrome).  Serum sodium levels should be monitored during maintenance therapy, and patients should be monitored for signs and symptoms possibly indicating hyponatremia such as nausea, malaise, headache, lethargy, confusion, obtundation, and increase in seizure frequency or severity.  If hyponatremia occurs, conservative measures such as fluid restriction, a reduction in dosage, or discontinuation of therapy will usually suffice.', '2', '', 'DDInter', 0);
