INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14292, 216, 'Oxymetazoline (nasal)', 'Diabetes Mellitus', 'Topically applied sympathomimetic agents are systemically absorbed, particularly during prolonged or indiscriminate use.  Slight increases in blood glucose concentrations may occur with the use of these drugs.  Therapy with topical sympathomimetic agents should be administered cautiously in patients with diabetes mellitus.  Closer monitoring of blood glucose concentrations may be appropriate.  It is important that the recommended dosages of the individual products not be exceeded.', '2', '', 'DDInter', 0);
