INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14401, 27945, 'Paclitaxel (protein-bound)', 'Liver Diseases', 'Paclitaxel is extensively metabolized by the liver.  Patients with moderate to severe hepatic impairment may be at increased risk for hepatotoxicity.  Additionally, myelotoxicity of paclitaxel may be exacerbated in patients with serum total bilirubin >2 times ULN.  Therapy with paclitaxel should be administered cautiously and at a reduced dosage in patients with compromised hepatic function.', '3', '', 'DDInter', 0);
