INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14409, 4454, 'Paclitaxel (protein-bound)', 'Bone Marrow Failure Disorders', 'Paclitaxel induces dose-dependent myelosuppression, primarily affecting neutrophils.  Anemia characterized as a red blood cell count <11 g/dl has been reported in 78% of patients administered paclitaxel.  Thrombocytopenia is uncommon and rarely severe.  Therapy with paclitaxel should be administered cautiously in patients whose bone marrow reserve may be severely depressed and should be withheld when neutrophil counts fall below 1500/mm3 and/or platelet counts fall below 100,000/mm3.  Paclitaxel injection should not be used in patients with solid tumors with baseline neutrophil counts less than 1500/mm3 or in patients with AIDS-related Kaposi''s sarcoma with baseline neutrophil counts of less than 1000/mm3.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitoring of hematopoietic function is recommended.', '3', '', 'DDInter', 0);
