INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14515, 151, 'Oxytetracycline', 'Kidney Diseases', 'Tetracyclines (except doxycycline) are eliminated by the kidney to various extent.  Patients with renal impairment may be at greater risk for tetracycline-associated hepatic and/or renal toxicity (increased BUN with consequent azotemia, hyperphosphatemia, and acidosis) due to decreased drug clearance.  Therapy with tetracyclines should be administered cautiously at reduced dosages in patients with renal impairment.  Clinical monitoring of renal and liver function is recommended, and serum tetracycline levels may be necessary during prolonged therapy.', '2', '', 'DDInter', 0);
