INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14646, 11118, 'Oxytocin', 'Edema', 'Volume overload can occur during administration of oxytocin.  Oxytocin possess antidiuretic properties.  Consideration should, therefore, be given to the possibility of volume overload and water intoxication, particularly when oxytocin is administered continuously by infusion and the patient is receiving fluids by mouth.', '2', '', 'DDInter', 0);
