INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14797, 6545, 'Paliperidone', 'Depressive Disorder', 'Adult and pediatric patients with depression and other psychiatric disorders may experience worsening of their symptoms and may have the emergence of suicidal thoughts and behavior.  Patients should be monitored appropriately and observed closely for worsening of their symptoms, suicidality or changes in their behavior, especially during the first few months of treatment, and at times of dose changes.  Discontinuing the medication should be considered if symptoms are persistently worse, or abrupt in onset.', '2', '', 'DDInter', 0);
