INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14833, 6545, 'Paliperidone', 'Intestinal Obstruction', 'An antiemetic effect was observed in preclinical studies with paliperidone.  This effect, if it occurs in humans, may mask the signs and symptoms of overdosage with certain drugs or of conditions such as intestinal obstruction, Reye''s syndrome, and brain tumor.  Cautions should be exercised when given paliperidone to these patients.', '2', '', 'DDInter', 0);
