INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14951, 29057, 'Panitumumab', 'Eye Diseases', 'Some epidermal growth factor receptor (EGFR) inhibitors should be administered cautiously in patients with ocular disorders such as primary keratitis, ulcerative keratitis, or conjunctivitis.  It is recommended to interrupt or discontinue treatment with these agents if patients present with acute or worsening ocular disorders.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14952, 29058, 'Panitumumab', 'Eye Diseases', 'Some epidermal growth factor receptor (EGFR) inhibitors should be administered cautiously in patients with ocular disorders such as primary keratitis, ulcerative keratitis, or conjunctivitis.  It is recommended to interrupt or discontinue treatment with these agents if patients present with acute or worsening ocular disorders.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14953, 29057, 'Panitumumab', 'Lung Diseases', 'The use of certain EGFR inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.', '3', '', 'DDInter', 0);
