INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15047, 818, 'Pantoprazole', 'Fractures, Bone', 'Various published observational studies have reported that PPI therapy may be associated with an increased risk for osteoporosis related fractures of the hip, wrist or spine.  The risk was increased in patients who received high doses (multiple daily doses), and long term treatment (a year or longer).  Patients should use the lowest dose and shortest duration of PPI therapy appropriate to the condition being treated.  Caution should be used in patients at risk for osteoporosis related fractures and should be managed according to established treatment guidelines.', '2', '', 'DDInter', 0);
