INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15298, 26950, 'Pasireotide', 'Adrenal Insufficiency', 'Treatment with somatostatin analogs may suppress the pituitary hormones other than GH/IGF-1.  It is recommended to monitor pituitary function prior to initiation of therapy and periodically during treatment, as clinically appropriate.  Patients should be monitored for and instructed on the signs and symptoms of adrenal insufficiency during therapy.  If adrenal insufficiency is suspected it should be confirmed and treated per standard of care with exogenous glucocorticoids at replacement doses.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15299, 26948, 'Pasireotide', 'Bradycardia', 'Bradycardia has been reported with the use of somatostatin analogs.  It is recommended to carefully monitor patients with cardiac disorders for the development of bradycardia.  Dose adjustments of beta-blockers, calcium channel blockers, or correction of electrolyte disturbances may be necessary.  Care should be taken when initiating treatment with these agents in patients with bradycardia.', '2', '', 'DDInter', 0);
